import { OscData } from '@jsnix/utils/types'

export const bin = ['div', 'html']
export const osc: OscData = {
	id: 80085,
	handler: async ({ data, terminal, document, container }) => {
		const marker = terminal.registerMarker(0)

		if (marker) {
			if (!container) {
				return false
			}
			const { html } = data as { html: string }
			const node = document.createElement('div')
			node.innerHTML = html
			node.style.visibility = 'hidden'
			node.style.position = 'absolute'
			node.style.top = '0'
			node.style.left = '0'
			node.style.zIndex = '1000'
			document.body.appendChild(node)

			const rect = node.getBoundingClientRect()
			const { height, width } = terminal.calculateDecorationSize(rect.width, rect.height)
			const decoration = terminal.registerDecoration({
				marker,
				width: terminal.cols,
				height,
				layer: 'top',
			})

			decoration?.onRender((e: HTMLElement) => {
				if (!Array.from(e.children).includes(node)) {
					node.style.visibility = 'visible'
					e.appendChild(node)
				}
			})
			return `${' '.repeat(width)}\n`.repeat(height - 1)
		}
		return false
	},
}

