import { loadJsnixExports, WrappedJsnixExports } from '@jsnix/utils/osc'

function App() {
	const [jsnixExports, setJsnixExports] = useState<WrappedJsnixExports[]>([])
	const options: JsnixOptions = {
		env: {
			PATH: '/home/workspace/bin:/bin:/usr/bin:/usr/local/bin',
			NODE_OPTIONS: '--no-warnings',
		},
		bootstrap: [
			{
				cmd: ['npm', 'i', '@jsnix/cli'],
				options: {
					cwd: '/bin',
				},
			},
			{
				cmd: ['ln', '-s', 'node_modules/@jsnix/cli/dist/cli/index.js', 'jsnix'],
				options: {
					cwd: '/bin',
				},
			},
		],
		// bootstrap: [
		//   {
		//     cmd: ["npm", "i"],
		//     options: {
		//       cwd: "/jsnix",
		//       output: false
		//     }
		//   },
		//   {
		//     cmd: ["npm", "run", "build"],
		//     options: {
		//       cwd: "/jsnix",
		//       output: false
		//     }
		//   },
		//   {
		//     cmd: ["ln", "-s", "../jsnix/packages/cli/dist/cli/index.js", "jsnix"],
		//     options: {
		//       cwd: "/bin",
		//       output: false
		//     }
		//   }
		// ],
		entrypoint: ['jsnix'],
		jsnixExports,
	}

	const loadSnapshot = async () => {
		const module = await import('virtual:@jsnix/snapshot')
		return {
			jsnix: {
				directory: module.default,
			},
			bin: {
				directory: {},
			},
		}
	}

	const loadExports = async () => {
		// TODO: patch Pastel to allow ignoring certain files as commands (rather than relying on hacking this with the _app prefix)
		const modules = import.meta.glob('../../cli/commands/**/_app/jsnix.{js,jsx,ts,tsx}')
		const exports = await loadJsnixExports(modules)
		setJsnixExports(exports.filter((module) => module !== null))
	}

	useEffect(() => {
		loadExports()
	}, [])

	return (
		<>
			<Jsnix
				fs={loadSnapshot}
				options={options}
			/>
		</>
	)
}

export default App

