        "lint": {
            "dependsOn": [
                "^lint"
            ]
        },
        "check-types": {
            "dependsOn": [
                "^check-types"
            ]
        },
        "dev": {
            "persistent": true,
            "cache": false
        },
        "clean": {
            "dependsOn": [
                "^clean"
            ]
        }
    }
}

